package java_basics;

public class FloatingPointTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float floatValue = 23.456789099399332f;
		float floatMax = Float.MAX_VALUE;
		float floatMin = Float.MIN_NORMAL;
		float floatPower = 1.2e7f;
		
		System.out.println(floatValue);
		System.out.println(floatMax);
		System.out.println(floatMin);
		System.out.println(floatPower);
		
		double doubleValue = 23.326888787979999999999999d;
		System.out.println(doubleValue);
	}

}
