package java_basics;

public class VariableWithInitializeForm {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		byte byteVal = 10;
		short shortVal = 1000;
		int intVal =10000;
		long longVal = 2000000l;
		float floatVal = 344.5f;
		double doubleVal = 40.83832929292d;
		char charVal = 'a';
		boolean boolVal = true;
		
		System.out.println("This is byteVal " + byteVal);
		System.out.println("This is shortVal " + shortVal);
		System.out.println("This is intVal " + intVal);
		System.out.println("This is longVal " + longVal);
		System.out.println("This is floatVal " + floatVal);
		System.out.println("This is doubleVal " + doubleVal);
		System.out.println("This is charVal " + charVal);
		System.out.println("This is boolean value " + boolVal);
	}

}
